            AddonFrame.print("|cffff0000>>Пауза<<", true);
        end
    end

    if modifier == "RCTRL" then
        if autoUseLure then
            autoUseLure = false;
            AddonFrame.print("|cffff0000>>Автонаживка не используется!<<", true);
        else
            autoUseLure = true;
            AddonFrame.print("|cff00ff00Автонживка!", true);
        end
    end
end

